mapper.delete:
/* 회의실코드로 회의실 삭제*/
DELETE
FROM
	tb_meet_reservation
WHERE
	reservation_num = #{reservationNum};

mapper.update:
/* 특정 회의실 수정 */
UPDATE tb_meet_reservation

	
		,reservation_date = #{reservationDate}
	 
	
		,reservation_start_time = #{reservationStartTime}
	 
	
		,reservation_end_time = #{reservationEndTime}
	 
	
		,reservation_contents = #{reservationContents}
	
	
		,reservation_update_member = #{reservationUpdateMember}
	
	
		,reservation_update_date = now()
			 

 WHERE
 	reservation_num = #{reservationNum};

mapper.delete:
/* 예약코드로 예약 삭제*/
DELETE
FROM
	tb_meet_reservation
WHERE
	reservation_num = #{reservationNum};

mapper.update:
/* 특정 회의실 수정 */
UPDATE tb_meetingroom

	
		,meet_usable_start = #{meetUsableStart}
	 
	
		,meet_usable_end = #{meetUsableEnd}
	 
	
		,meet_location = #{meetLocation}
	 
	
		,meet_capacity = #{meetCapacity}
	 
	
		,meet_detail = #{meetDetail}
	

 WHERE
 	meet_num = #{meetNum};

mapper.delete:
/* 회의실코드로 회의실 삭제*/
DELETE
FROM
	tb_meetingroom
WHERE
	meet_num = #{meetNum};
